import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { Jumbotron, Button, useAccordionToggle, Accordion, Card } from 'react-bootstrap';


import Companies from './Companies';
import Car from './Car';
import { Fragment } from 'react';

export default function Customers(){

  const [companyList, setCompanyList] = useState([]);
  const [carList, setCarList] = useState([]);
  const [uniques, setUniques] = useState([])
  const [loading, setloading] = useState(true);
  const [search, setSearch] = useState({
    companyName: null
  })

  const filter = () => {
    axios.post("http://localhost:8080/searchCompanies/", search)
      .then(res => setCompanyList(res.data))
  }

  useEffect(() => {
    axios.get("http://localhost:8080/getAllCompanies")
      .then(res => {
        setCompanyList(res.data)
        setUniques(res.data)})
      .then(setloading(false))
  }, []);
  
  if(!loading){
  return(
    <Fragment>
      <Jumbotron>
        <h1>Hello, world!</h1>
        <p>
          Slappa da bass
        </p>
        <p>
          <Button variant="primary">Learn more</Button>
        </p>
      </Jumbotron>
      <div>
        <Companies companyList={companyList} setCompanyList={setCompanyList.bind(this)} />
        <select name="company" onChange={e => { setSearch(search, search.companyName=e.target.value);filter();}}>
            <option key={null} value={null}>Alla</option>
            {Array.from(new Set(uniques.map(x => x.companyName))).map(type => 
              <option key={type}>{type}</option>
            )}
        </select>  
      </div>
      
      <Accordion defaultActiveKey="0">
        <Card>
          <Accordion.Toggle as={Card.Header} eventKey="0">
            Click me!
          </Accordion.Toggle>
          <Accordion.Collapse eventKey="0">
            <Card.Body>Hello! I'm the body
              
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    </Fragment>
  ) 
}
  else{
    return(
      <div>
        <h1>Loading companies...</h1>
      </div>
    )
  }
}
